import React, {useContext} from 'react';
import styles from './ItemMode.module.css';
import { AppContext } from '../../context/AppContext';

export const ItemTotal = ({item, title, remove, editable, showTotal}) => {
    const active = item.modules && item.modules.total ? true : false;

    const { dispatch } = useContext(AppContext);

    const handleChange = (e) => {
        item.modules.total.value = e.target.value;
        updateItem() 
    }
    const updateItem = () => {
        dispatch({
            type:'UPDATE_ITEM',
            payload: item
        });
    }
    const handleRemove = () => {
        delete item.modules.total
        dispatch({
            type:'UPDATE_ITEM',
            payload: item
        });
        showTotal(false)
    }
    return (
        <>
            { active &&
            <div className={styles.modeContainer}>
                {title && <h5>Total</h5>}
                <span>
                    { editable ? (
                    <input className={styles.modeInput} type="text" name="total" value={item.modules.total.value.toFixed(2)} onChange={handleChange} />    
                    ) : (
                    <span>{item.modules.total.value.toFixed(2)}</span>
                    )}
                    
                </span>
                {remove && <i onClick={handleRemove} className={`material-icons ${styles.iconClose}`}>close</i>}
            </div>}
        </>
    )
}
