import React, {useState,useContext} from 'react'
import styles from './ItemDetail.module.css';
import { Link } from 'react-router-dom';
import { AppContext } from '../../context/AppContext';

export const ItemListItem = ({item}) => {
    const [checked, setChecked] = useState(item.status || false)

    const { dispatch } = useContext(AppContext);
    
    const handleChecked = (e) => {
        const check = e.target.checked;
        const listItem = item;
        listItem.status = check;
        dispatch({
            type:'UPDATE_ITEM',
            payload: listItem
        });
        setChecked(!checked)
    }

    return (
        <div>
            <label className={styles.checkBox}><input type="checkbox" className="filled-in" checked={checked} onChange={handleChecked} /><span></span></label>
            <h6 className={styles.heading} style={checked ? {textDecoration:"line-through"} : {textDecoration:"none"}}><Link to={`/item/${item.id}`} as={`/item/${item.id}`}>{item.title}</Link></h6>
            <i className={`material-icons right ${styles.iconHidden}`}>edit</i>
        </div>
    )
}
