import React, {useContext} from 'react'
import { AppContext } from '../context/AppContext';
import { TreeCrawl } from '../components/TreeCrawl';

export const ListTree = () => {
    const { items } = useContext(AppContext);

    const topLevel = [...items].filter(i => null === i.parent)

    const tree = (parent, items, found) => {
        for(var i in items) {
            if (items[i].parent === parent && ! found.includes(items[i]) ) {
                found.push(items[i]);
                tree(items[i].id, items, found)
            } 
        }
        return found
    }
    const fullTree = tree(null, items, [])

    // var nestedItems = {
    //     1:{title:'bla',children:{
    //         3:{title:'bla'},
    //         5:{title:'blahaha'}}
    //     },
    //     2:{title:'bla',children:{}}
    // }
    
    const build = () => {
        const added = []
        const crawl = (parent) => {
            var found = {}
            for ( var i in items) {
                var item = items[i];
                if (parent === item.parent && ! added.includes(item.id)) {
                    added.push(item.id);
                    found[item.id] = {title:item.title}
                    var children = crawl(item.id)
                    if (children.length > 0) found[item.id].children = children                    
                } 
            }
            return found
        }
        return crawl(null)
    }
    console.log('build',build())

    var mappedItems = []
    return (
        <div className="container">
            <ul>
            {topLevel.map((item)=>{
                if (item.parent === null && ! mappedItems.includes(item.id)) {
                    console.log('topLevel',item.id)
                    mappedItems.push(item.id)
                    return (
                    <li key={item.id}>
                        <span>{item.title}</span>
                        <TreeCrawl mappedItems={mappedItems} parent={item} items={items} />
                    </li>)
                } else  {
                    return ('')
                }                
            })}
            </ul>
        </div>
    )
}
