import React, {useState, useContext} from 'react';
import styles from './ItemCard.module.css';
import { Link } from 'react-router-dom';
import { useClickOutside} from '../Helpers/ClickOutside';
import { AppContext } from '../../context/AppContext';


export default function ItemCardItem({item}) {
    const [itemMenu, setItemMenu] = useState(false);

    const { items, dispatch } = useContext(AppContext);

    const nextId = () => items.reduce((id, item)=>( item.id > id ? item.id : id), 0) + 1

    const handleCopy = () =>{
        console.log('item.id',item.id)
        const copy = {...item}
        console.log('copy',copy)
        copy.id = nextId()
        console.log('item',item)
        console.log('copy.id',copy.id)
        // dispatch({
        //     type:'DELETE_ITEM',
        //     payload: item
        // });
        setItemMenu(false)
    }

    const handleDelete = () => {
        dispatch({
            type:'DELETE_ITEM',
            payload: item
        });
        setItemMenu(false)
    }

    let domMenu = useClickOutside(() => {
        setItemMenu(false)
    })
    return (
        <>  
            {itemMenu &&
            <div ref={domMenu} className={`z-depth-5 ${styles.menuWrap}`}>
                <ul>
                    <li><i onClick={() => setItemMenu(false)}className={`material-icons ${styles.icon}`}>close</i></li>
                    <li onClick={handleCopy}>Copy</li>
                    <li>Move</li>
                    <li onClick={handleDelete}>Delete</li>
                </ul>
            </div>             
            }
      

            <h6 className={styles.heading}><Link to={`/item/${item.id}`}>{item.title}</Link></h6>
            <i onClick={()=>setItemMenu(true)} className={`material-icons right ${styles.iconSmall}`}>edit</i>

        </>
    )
}
