import React, {useState} from 'react'
import styles from './ItemDetail.module.css';

export const ItemListItem = (item, state) => {
    const [checked, setChecked] = useState(item.status || false)

    const handleChecked = () => {
        setChecked(!checked)
    }

    return (
        <div>
            <label onClick={handleChecked} className={styles.checkBox}><input type="checkbox" className="filled-in" checked={checked} /><span></span></label>
            <h6 className={styles.heading}>{item.title}</h6>
            <i className={`material-icons right ${styles.iconHidden}`}>edit</i>
        </div>
    )
}
