import React from 'react';
import styles from './ItemCard.module.css';
import { Link } from 'react-router-dom';

export default function ItemCardItem({item}) {

    return (
        <>
            <div className={`z-depth-5 ${styles.menuWrap}`}>
                <ul>
                    <li><i className={`material-icons ${styles.icon}`}>close</i></li>
                    <li>Copy</li>
                    <li>Move</li>
                    <li>Delete</li>
                </ul>
            </div>       

            <h6 className={styles.heading}><Link to={`/item/${item.id}`}>{item.title}</Link></h6>
            <i className={`material-icons right ${styles.iconSmall}`}>edit</i>

        </>
    )
}
