import React, {useState} from 'react'
import { Link } from 'react-router-dom'
import styles from './Nav.module.css'


export default function Nav({children, logo}) {
    const [menu, setmenu] = useState(false)
    return (
        <div className="navbar-fixed">
            <nav className={styles.nav}>
                <div className="nav-wrapper container">
                    <Link to="/" className={`brand-logo ${styles.brand}`}>{logo}</Link>
                    <a href onClick={()=>setmenu(true)} className="sidenav-trigger"><i className="material-icons">menu</i></a>
                    <ul className="right hide-on-med-and-down">
                    {children}
                    </ul>
                </div>
            </nav>           
            <ul className="sidenav"  style={{transform:menu ? "translateX(0%)":"translateX(-105%)" }}>
            {children}
            </ul>
        </div>

    )
}
