import React, {useState} from 'react';
import styles from './ItemCard.module.css';


export default function ItemCard({item}) {
    const [itemInput, setitemInput] = useState(item)
    const [itemEdit, setitemEdit] = useState(false)

    const handleChange = (e) => {
        const [name, value] = [e.target.name, e.target.value]
        setitemInput(prev => {
            return { ...prev, [name]:value }
        })
    }

    return (
    <div className={styles.warp}>
        { itemEdit ? (
        <>
            <div className={styles.underlay} />
            <div className={`card-panel ${styles.modal}`}>
                
                <form autoComplete="off">                                
                    <input
                        type="text"
                        placeholder="Title"
                        value={itemInput.title}
                        name='title'
                        className="item-input"
                        onChange={handleChange}
                    />
                    <textarea 
                        type="text"
                        placeholder="Description"
                        value={itemInput.description}
                        name='description'
                        className="materialize-textarea"
                        onChange={handleChange}
                    />
                    <button className="btn btn-full">Save</button>
                </form>
            </div>
        </>
        ):(
        <div className="card-panel">
            <h5 className={styles.heading}>{item.title}</h5>
            <i onClick={() => setitemEdit(true)} className={`material-icons right ${styles.icon}`}>edit</i>
        </div>            
        )}
    </div>
    )
}
