import React, {useContext} from 'react'
import { AppContext } from '../context/AppContext';
import { TreeCrawl } from '../components/TreeCrawl';

export const ListTree = () => {
    const { items } = useContext(AppContext);

    const topLevel = [...items].filter(i => null === i.parent)

    var mappedItems = []
    return (
        <div className="container">
            <ul>
            {topLevel.map((item)=>{
                if (item.parent === null && ! mappedItems.includes(item.id)) {
                    mappedItems.push(item.id)
                    return (
                    <li key={item.id}>
                        <span>{item.title}</span>
                        <TreeCrawl mappedItems={mappedItems} parent={item} items={items} />
                    </li>)
                } else  {
                    return ('')
                }                
            })}
            </ul>
        </div>
    )
}
