import React, {useState, useContext} from 'react';
import { AppContext } from '../../context/AppContext';
import styles from './ItemCard.module.css';
import ItemCardItem from './ItemCardItem';


export default function ItemCard({item, items}) {
    const [itemInput, setitemInput] = useState(item)
    const [itemEdit, setitemEdit] = useState(false)

    const [editTitle, setEditTitle] = useState({show:false, input:item.title})
    const [updateTitle, setUpdateTitle] = useState(item.title)

    const [addItem, setAddItem] = useState(false)
    const { dispatch } = useContext(AppContext);
    const childItems = [...items].filter(i => item.id === i.parent)
    
    const handleTitleChange = (e) => {
        console.log(e)
        // if (! formInput.title || /^\s*$/.test(formInput.title)) return

        // const item = formInput;
        // item.id = nextId();

        // dispatch({
        //     type:'ADD_ITEM',
        //     payload: item
        // });
        // setItemTitle(false)
    }

    const handleChange = (e) => {
        const [name, value] = [e.target.name, e.target.value]
        setitemInput(prev => {
            return { ...prev, [name]:value }
        })
    }

    const handleAddItem = () => {
        setAddItem(!addItem)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
    }

    return (
    <div className={styles.warp}>
        <div className="card-panel">
            {editTitle.show ? (
            <input type="text" value={updateTitle} name='title' className={styles.inputSmall} onChange={handleTitleChange}/>
            ) : (
            <h5 onClick={() => setEditTitle(p=>{return{...p,show:true}})} className={styles.heading}>{item.title}</h5>)}
            <i onClick={() => setitemEdit(true)} className={`material-icons right ${styles.icon}`}>more_horiz</i>
            <ItemCardItem childItems={childItems} />
            <div className={addItem ? 'active' : ''}>
                <div className={`collapsible-body card-panel ${styles.collapsibleBody}`} style={addItem ? {display:'block'}:{}}>
                    <input
                        type="text"
                        placeholder="Title"
                        value='default'
                        name='title'
                        className={styles.inputSmall}
                        onChange={handleChange}
                    />
                </div>
                <button onClick={handleAddItem} className="btn secondary">{addItem ? 'Save item' : 'Add item'}</button>
                {addItem && <i onClick={() => setAddItem(false)} className={`material-icons right ${styles.iconClose}`}>close</i>}
            </div>
        </div>
    </div>
    )
}
