import React, {useState, useContext} from 'react';
import { AppContext } from '../../context/AppContext';
import styles from './ItemCard.module.css';
import ItemCardItem from './ItemCardItem';
import { useClickOutside } from '../Helpers/ClickOutside';

export default function ItemCard({item, items}) {
    const [itemInput, setitemInput] = useState(item)
    const [itemMenu, setItemMenu] = useState(false)

    const [editTitle, setEditTitle] = useState(false)

    const [addItem, setAddItem] = useState(false)
    const { dispatch } = useContext(AppContext);
    const childItems = [...items].filter(i => item.id === i.parent)
    
    const handleTitleSave = (e) => {
        if (e.charCode === 13) {
            if (! itemInput.title || /^\s*$/.test(itemInput.title)) return

            const item = itemInput;

            dispatch({
                type:'UPDATE_ITEM',
                payload: itemInput
            });
            setEditTitle(false)
        }
    }

    const handleChange = (e) => {
        const [name, value] = [e.target.name, e.target.value]
        setitemInput(prev => {
            return { ...prev, [name]:value }
        })
    }

    const handleAddItem = () => {
        setAddItem(!addItem)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
    }

    let domTitle = useClickOutside(() => {
        setEditTitle(false)
    })

    return (
    <div className={styles.warp}>
        <div className={styles.menuWrap}>
            <ul>
                <li>Add</li>
                <li>Copy</li>
                <li>Move</li>
                <li>Delete</li>
            </ul>
        </div>
        <div className="card-panel">
            {editTitle ? (
            <input ref={domTitle} type="text" value={itemInput.title} name='title' className={styles.inputSmall} onChange={handleChange} onKeyPress={handleTitleSave}/>
            ) : (
            <h5 onClick={() => setEditTitle(true)} className={styles.heading}>{item.title}</h5>)}
            <i onClick={() => setItemMenu(true)} className={`material-icons right ${styles.icon}`}>more_horiz</i>
            <ItemCardItem childItems={childItems} />
            <div className={addItem ? 'active' : ''}>
                <div className={`collapsible-body card-panel ${styles.collapsibleBody}`} style={addItem ? {display:'block'}:{}}>
                    <input
                        type="text"
                        placeholder="Title"
                        value='default'
                        name='title'
                        className={styles.inputSmall}
                        onChange={handleChange}
                    />
                </div>
                <button onClick={handleAddItem} className="btn secondary">{addItem ? 'Save item' : 'Add item'}</button>
                {addItem && <i onClick={() => setAddItem(false)} className={`material-icons right ${styles.iconClose}`}>close</i>}
            </div>
        </div>
    </div>
    )
}
