import React from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Nav from '../components/universal/Nav/Nav';
import { AppProvider, } from '../context/AppContext';
import Dashboard from './Dashboard';
import Item from './Item';
import NavLinks from '../components/universal/Nav/NavLinks';

const links = [
    {href:'/',title:'Home'},
    {href:'/lists',title:'Lists'}
]

export default function Base() {

    return (
    <AppProvider>
        <Router>
            
            <Nav links={NavLinks} logo={(<><i className="material-icons">blur_on</i><span>Magic</span></>)} />
            <Switch> 
                <Route path="/" exact>
                    <Dashboard />
                </Route>
                <Route path="/item/:itemId" exact>
                    <Item />
                </Route>      
            </Switch> 
        </Router>     
    </AppProvider>
    )
}
