import React, {useState} from 'react';
import styles from './ItemCard.module.css';


export default function ItemCard({item, items}) {
    const [itemInput, setitemInput] = useState(item)
    const [itemEdit, setitemEdit] = useState(false)



    const childItems = [...items].filter(i => item.id === i.parent)
    console.log('childItems',items,childItems)

    const handleChange = (e) => {
        const [name, value] = [e.target.name, e.target.value]
        setitemInput(prev => {
            return { ...prev, [name]:value }
        })
    }
    const handleSubmit = (e) => {
        e.preventDefault();
    }

    return (
    <div className={styles.warp}>
        { itemEdit ? (
        <>
            <div onClick={() => setitemEdit(false)} className={styles.underlay} />
            <div className={`card-panel ${styles.modal}`}>                
                <form autoComplete="off" onSubmit={handleSubmit}>                                
                    <input
                        type="text"
                        placeholder="Title"
                        value={itemInput.title}
                        name='title'
                        className="item-input"
                        onChange={handleChange}
                    />
                    <textarea 
                        type="text"
                        placeholder="Description"
                        value={itemInput.description}
                        name='description'
                        className="materialize-textarea"
                        onChange={handleChange}
                    />
                    <button className="btn btn-full">Save</button>
                </form>
            </div>
            <div className={styles.editOptions}>
                <ul>
                    <li>Copy</li>
                    <li>Move</li>
                    <li>Delete</li>
                </ul>
            </div>
        </>
        ):(
        <div className="card-panel">
            <h5 className={styles.heading}>{item.title}</h5>
            <i onClick={() => setitemEdit(true)} className={`material-icons right ${styles.icon}`}>more_horiz</i>
            {childItems.map((i) => (
                <div key={i.id} className={`card-panel ${styles.childItem}`}>
                    <h6 className={styles.heading}>{i.title}</h6>
                    <i onClick={() => setitemEdit(true)} className={`material-icons right ${styles.icon}`}>more_horiz</i>
                </div>
            ))}
        </div>            
        )}
    </div>
    )
}
