import { createContext, useReducer, useEffect } from "react";

const AppReducer = (state, action) => {
    switch (action.type) {
        case 'ADD_ITEM':
            return {
                ...state, 
                items: [...state.items, action.payload],
            }
        case 'UPDATE_ITEM':
            return {
                ...state, 
                items: state.items.map(
                    (item) => item.id === action.payload.id ? action.payload : item
                )
            }  
        case 'DELETE_ITEM':
            return {
                ...state, 
                items: [...state.items].filter((item) => {
                    if (item.id !== action.payload.id && item.parent !== action.payload.id) {
                        return true;
                    } else if (item.parent === action.payload.id) {
                        item.parent = null;
                        return true;
                    }
                    return false;
                })
            } 
        case 'ADD_TAG':
            return {
                ...state, 
                tags: [...state.tags, action.payload],
            }
        case 'DELETE_TAG':
            return {
                ...state, 
                tags: [...state.tags].filter(tag => tag.id !== action.payload.id),
                items: [...state.items, state.items.map( (item) => {
                    if (item.tags && item.tags.includes(action.payload)) {
                        item.tags = item.tags.filter(tag => tag !== action.payload.id) }
                    return item
                })] 
            }        
        default:
            return state;
    }
}
const KEY = 'list-app';
const getStore = () =>{
    return  JSON.parse(localStorage.getItem(KEY));
}
const setStore = (data) =>{
    localStorage.setItem(KEY, JSON.stringify(data));
}

const initialState = {
    tags: [
        {id:1, title:'Personal'}
    ],
    items:[
        {id:1, title:'Title', description:'This is a general description', tags:[], parent:null},
        {id:2, title:'Title 2', description:'This is a general description 2', tags:[], parent:null},
        {id:3, title:'Title 3', description:'This is a general description 3', tags:[], parent:2},
    ]
}


export const AppContext = createContext();

export const AppProvider = (props) =>{
    const [state, dispatch] = useReducer(AppReducer, getStore() || initialState);

    useEffect(() => {
        setStore(state)
    }, [state])

    return (
    <AppContext.Provider 
        value={{
            items: state.items,
            tags: state.tags,
            dispatch,
        }}
    >
        {props.children}
    </AppContext.Provider>)
}
