import React, {useContext} from 'react'
import { AppContext } from '../context/AppContext';
import { TreeCrawl } from '../components/TreeCrawl';

export const ListTree = () => {
    const { items } = useContext(AppContext);

    const topLevel = [...items].filter(i => null === i.parent)

    var mappedItems = []
    return (
        <div className="container">
            <ul>
            {topLevel.map((item)=>{
                if (item.parent === null) {
                    mappedItems.push(item)
                    return (<li key={item.id}><span>{item.title}</span></li>)
                } else  {
                    mappedItems.push(item)
                    return (<li key={item.id}><span>{item.title}</span></li>)
                    // return (<li key={item.id}><span>{item.title}</span><TreeCrawl mappedItems={mappedItems} items={items} /></li>)
                }                
            })}
            </ul>
        </div>
    )
}
