import React, {useContext, useState} from 'react';
import { useParams } from 'react-router';
import { AppContext } from '../context/AppContext';
import styles from '../components/ItemDetail/ItemDetail.module.css';

export default function Item() {
    let { itemId } = useParams();
    itemId= Number(itemId)
    const { items } = useContext(AppContext);
    const item = [...items].find(item => item.id === itemId)

    const [textAreaHeight, setTextAreaHeight] = useState('7rem')
    
    const [itemEdit, setItemEdit] = useState(item)   

    if (! item) return (<div>No matching item</div>)

    const children = [...items].filter(item => item.parent === itemId)

    const handleChange = (e) => {
        const [name, value] = [e.target.name, e.target.value] 
        setItemEdit(prev => {
            return { ...prev, [name]:value }
        })
        if (name === 'description') {
            console.log(e.target.scrollHeight)
            setTextAreaHeight(e.target.scrollHeight > 84 ? e.target.scrollHeight + 'px' : "7rem" )
        }
    }
    return (
        <div className="container">
            <div className={`card-panel ${styles.detailCard}`}>
                <form>
                    <div className="row">
                        <div className="col s12 m8">
                            <input
                                type="text"
                                placeholder="Title"
                                value={itemEdit.title}
                                name='title'
                                onChange={handleChange}
                            />
                            <textarea
                                type="text"
                                style={{height:textAreaHeight}}
                                className={`materialize-textarea ${styles.textArea}`}
                                placeholder="Description"
                                value={itemEdit.description}
                                name='description'
                                onChange={handleChange}
                            >
                            </textarea>
                        </div>
                        <div className="col s12 m4">

                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}
